import React,{Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart.js'

class App extends Component {
  constructor(){
    super();
    this.state = {
      chartData:{}
    }
  }

  componentWillMount(){
    this.getChartData();
  }

  getChartData(){
    //AJAX CALL HERE
    this.setState({
      chartData:{
        labels: ["Oct","Nov","Dec","Jan","Feb","Mar","Apr","May"],
        datasets:[
           {
              backgroundColor: "white",
              borderColor: "2E48EE",
              pointBackgroundColor: "2E48EE",
              pointRadius:4,
              lineTension: 0,
               label: 'Population',
               data:[
                40,
                60,
                80,
                20,
                40,
                30,
                70,
                80,
                60,
                20,
                90,
                10,
                60,
                50,
                40,
               ],

           }
        ]
    }
    });
  }

  render(){
  return (
    <div className="App">

      <Chart chartData = {this.state.chartData} stat="Points"/>
    </div>
  );
  }
}

export default App;
