import React,{Component} from 'react';
import {Line, Bar} from 'react-chartjs-2';

class Chart extends Component{
    constructor(props){
        super(props);
        this.state = {
            chartData:props.chartData
        }
    }

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        legendPosition:'left',
        stat:'Points',
    }

    render(){
        return(
            <form onSubmit = {this.props.getAllStats}>
                <button>
                    Refresh
                </button>
            </form>
        )
    }
}

export default Chart;
