import React,{Component} from 'react';
import {Line, Bar} from 'react-chartjs-2';

class Chart extends Component{
    constructor(props){
        super(props);
        this.state = {
            chartData:{
                labels: ["Boston","Wrcester","Springfield","Ottawa","Toronto"],
                datasets:[
                   {
                       label: 'Population',
                       data:[
                        10000,
                        11000,
                        12000,
                        13000,
                        14000,
                        15000
                       ],
                       backgroundColor: "green"
                   }
                ]
            }
        }
    }

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        legendPosition:'right',
    }

    render(){
        return(
            <div className = "chart">
                <Bar
                    data = {this.state.chartData}
                    options={{
                        title:{
                            display:this.props.displayTitle,
                            text:'Largest Cities',
                            fontSize: 25,
                        },
                        legend:{
                            display:true,
                            potition:'right',
                        }
                    }}
                />
            </div>
        )
    }
}

export default Chart;
