import React,{Component} from 'react';
import {Line, Bar} from 'react-chartjs-2';

class Chart extends Component{
    constructor(props){
        super(props);
        this.state = {
            chartData:props.chartData
        }
    }

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        legendPosition:'left',
        stat:'Points',
    }

    render(){
        return(
            <div className = "chart">
                <form onSubmit = {this.props.getAllStats}>
                <input type = "text" name = "season" placeholder = "Year..."/>
                <input type = "text" name = "stat" placeholder = "Statistic..."/>
                <button>
                    Refresh
                </button>
                </form>
                <Line 
                    data = {this.state.chartData}
                    options={{
                        title:{
                            display:this.props.displayTitle,
                            text:'18 - 19 ' + this.props.stat,
                            fontSize: 25,
                        },
                        legend:{
                            display:this.props.displayLegend,
                            potition:this.props.legendPosition,
                        },
                        scales: {
                            xAxes: [{
                                gridLines: {
                                    display: false
                                }
                            }],
                            yAxes: [{
                                gridLines: {
                                    display: false
                                }
                            }]
                        },
                    }}


                />
            </div>
        )
    }
}

export default Chart;
